import handle from "../handle.js";

const monorail = async (px) => {
  const walletData = await handle(px);
  try {
    const data = `${walletData.account.address.replace(
      /^0x/,
      ""
    )}`;
    const contract = "0xC995498c22a012353FAE7eCC701810D673E25794";
    const estimateGas = await walletData.wallet.eth.estimateGas({
      from: walletData.account.address,
      nonce: await walletData.wallet.eth.getTransactionCount(
        walletData.account.address
      ),
      to: contract,
      data: data,
      value: walletData.wallet.utils.toWei("0.1", "ether"),
    });
    const tx = {
      from: walletData.account.address,
      nonce: await walletData.wallet.eth.getTransactionCount(
        walletData.account.address
      ),
      to: contract,
      data: data,
      value: walletData.wallet.utils.toWei("0.1", "ether"),
      gas: estimateGas,
      gasPrice: await walletData.wallet.eth.getGasPrice(),
    };
    const signedTx = await walletData.account.signTransaction(
      tx,
      walletData.privateKey
    );
    const receipt = await walletData.wallet.eth.sendSignedTransaction(
      signedTx.rawTransaction
    );
    return `Successfully Swap Monad to Chog on monorail: https://testnet.monadexplorer.com/tx/${receipt.logs[0].transactionHash}`;
  } catch (error) {
    return `Error swap on monorail on wallet ${walletData.account.address}`;
  }
};

export default monorail;
